mapper.select:
select
  user_id, dept_id, username, nick_name, gender, phone, email, avatar_name, avatar_path, password, is_admin, enabled, create_by, update_by, pwd_reset_time, create_time, update_time
from sys_user


        and user_id = #{userId}


        and dept_id = #{deptId}


        and username = #{username}


        and nick_name = #{nickName}


        and gender = #{gender}


        and phone = #{phone}


        and email = #{email}


        and avatar_name = #{avatarName}


        and avatar_path = #{avatarPath}


        and password = #{password}


        and is_admin = #{isAdmin}


        and enabled = #{enabled}


        and create_by = #{createBy}


        and update_by = #{updateBy}


        and pwd_reset_time = #{pwdResetTime}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_user


        and user_id = #{userId}


        and dept_id = #{deptId}


        and username = #{username}


        and nick_name = #{nickName}


        and gender = #{gender}


        and phone = #{phone}


        and email = #{email}


        and avatar_name = #{avatarName}


        and avatar_path = #{avatarPath}


        and password = #{password}


        and is_admin = #{isAdmin}


        and enabled = #{enabled}


        and create_by = #{createBy}


        and update_by = #{updateBy}


        and pwd_reset_time = #{pwdResetTime}


        and create_time = #{createTime}


        and update_time = #{updateTime}
