mapper.select:
select
  notice_id, notice_imageurl, notice_content, notice_time, notice_place
from travel_notice


        and notice_id = #{noticeId}


        and notice_imageurl = #{noticeImageurl}


        and notice_content = #{noticeContent}


        and notice_time = #{noticeTime}


        and notice_place = #{noticePlace}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from travel_notice


        and notice_id = #{noticeId}


        and notice_imageurl = #{noticeImageurl}


        and notice_content = #{noticeContent}


        and notice_time = #{noticeTime}


        and notice_place = #{noticePlace}
